import React, { useState } from 'react';
import QRCode from 'qrcode.react';
import './QRCodeComponent.css';

const QRCodeComponent = () => {
  const [activeTab, setActiveTab] = useState('text');
  const [text, setText] = useState('');
  const [vCardData, setVCardData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    address: '',
    organization: '',
    title: '',
  });

  const handleInputChange = (event) => {
    setText(event.target.value);
  };

  const handleVCardInputChange = (event) => {
    const { name, value } = event.target;
    setVCardData({ ...vCardData, [name]: value });
  };

  const generateVCardString = () => {
    const { firstName, lastName, phone, email, address, organization, title } = vCardData;
    return `BEGIN:VCARD
VERSION:3.0
FN:${firstName} ${lastName}
ORG:${organization}
TITLE:${title}
TEL;TYPE=WORK,VOICE:${phone}
ADR;TYPE=WORK:;;${address}
EMAIL:${email}
END:VCARD`;
  };

  const vCardString = generateVCardString();
  const logoSrc = 'logo.png'; // Replace with the path to your logo image

  return (
    <div className="qr-code-container">
      <h1 className="title">QR Code Generator</h1>
      <div className="tabs">
        <button className={`tab ${activeTab === 'text' ? 'active' : ''}`} onClick={() => setActiveTab('text')}>Text</button>
        <button className={`tab ${activeTab === 'vcard' ? 'active' : ''}`} onClick={() => setActiveTab('vcard')}>vCard</button>
      </div>

      {activeTab === 'text' && (
        <div>
          <input 
            type="text" 
            value={text} 
            onChange={handleInputChange} 
            placeholder="Enter text or URL" 
            className="input-field"
          />
          {text && (
            <div className="qr-code">
              <QRCode value={text} size={256} />
              <img
                src={logoSrc}
                alt="Logo"
                className="qr-code-logo"
              />
            </div>
          )}
        </div>
      )}

      {activeTab === 'vcard' && (
        <div>
          <input
            type="text"
            name="firstName"
            value={vCardData.firstName}
            onChange={handleVCardInputChange}
            placeholder="First Name"
            className="input-field"
          />
          <input
            type="text"
            name="lastName"
            value={vCardData.lastName}
            onChange={handleVCardInputChange}
            placeholder="Last Name"
            className="input-field"
          />
          <input
            type="text"
            name="phone"
            value={vCardData.phone}
            onChange={handleVCardInputChange}
            placeholder="Phone"
            className="input-field"
          />
          <input
            type="email"
            name="email"
            value={vCardData.email}
            onChange={handleVCardInputChange}
            placeholder="Email"
            className="input-field"
          />
          <input
            type="text"
            name="address"
            value={vCardData.address}
            onChange={handleVCardInputChange}
            placeholder="Address"
            className="input-field"
          />
          <input
            type="text"
            name="organization"
            value={vCardData.organization}
            onChange={handleVCardInputChange}
            placeholder="Organization"
            className="input-field"
          />
          <input
            type="text"
            name="title"
            value={vCardData.title}
            onChange={handleVCardInputChange}
            placeholder="Title"
            className="input-field"
          />
          {vCardString && (
            <div className="qr-code">
              <QRCode value={vCardString} size={256} />
              <img
                src={logoSrc}
                alt="Logo"
                className="qr-code-logo"
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default QRCodeComponent;
