import React, { useState } from 'react';
import QRCode from 'qrcode.react';

const QRCodeComponent = () => {
  const [vCardData, setVCardData] = useState({
    firstName: '',
    lastName: '',
    phone: '',
    email: '',
    address: '',
    organization: '',
    title: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setVCardData({ ...vCardData, [name]: value });
  };

  const generateVCardString = () => {
    const { firstName, lastName, phone, email, address, organization, title } = vCardData;
    return `BEGIN:VCARD
VERSION:3.0
FN:${firstName} ${lastName}
ORG:${organization}
TITLE:${title}
TEL;TYPE=WORK,VOICE:${phone}
ADR;TYPE=WORK:;;${address}
EMAIL:${email}
END:VCARD`;
  };

  const vCardString = generateVCardString();

  const logoSrc = 'path-to-your-logo.png'; // Replace with the path to your logo image

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>vCard QR Code Generator</h1>
      <input
        type="text"
        name="firstName"
        value={vCardData.firstName}
        onChange={handleInputChange}
        placeholder="First Name"
        style={{ padding: '10px', width: '300px', marginBottom: '10px' }}
      />
      <input
        type="text"
        name="lastName"
        value={vCardData.lastName}
        onChange={handleInputChange}
        placeholder="Last Name"
        style={{ padding: '10px', width: '300px', marginBottom: '10px' }}
      />
      <input
        type="text"
        name="phone"
        value={vCardData.phone}
        onChange={handleInputChange}
        placeholder="Phone"
        style={{ padding: '10px', width: '300px', marginBottom: '10px' }}
      />
      <input
        type="email"
        name="email"
        value={vCardData.email}
        onChange={handleInputChange}
        placeholder="Email"
        style={{ padding: '10px', width: '300px', marginBottom: '10px' }}
      />
      <input
        type="text"
        name="address"
        value={vCardData.address}
        onChange={handleInputChange}
        placeholder="Address"
        style={{ padding: '10px', width: '300px', marginBottom: '10px' }}
      />
      <input
        type="text"
        name="organization"
        value={vCardData.organization}
        onChange={handleInputChange}
        placeholder="Organization"
        style={{ padding: '10px', width: '300px', marginBottom: '10px' }}
      />
      <input
        type="text"
        name="title"
        value={vCardData.title}
        onChange={handleInputChange}
        placeholder="Title"
        style={{ padding: '10px', width: '300px', marginBottom: '20px' }}
      />
      <div style={{ position: 'relative', display: 'inline-block' }}>
        {vCardString && <QRCode value={vCardString} size={256} />}
        {vCardString && (
          <img
            src={logoSrc}
            alt="Logo"
            style={{
              position: 'absolute',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              width: '20%',
              height: '20%',
              borderRadius: '10%',
            }}
          />
        )}
      </div>
    </div>
  );
};

export default QRCodeComponent;
